/* @flow */

import config from '../config'
import { initProxy } from './proxy'
import { initState } from './state'
import { initRender } from './render'
import { initEvents } from './events'
import { mark, measure } from '../util/perf'
import { initLifecycle, callHook } from './lifecycle'
import { initProvide, initInjections } from './inject'
import { extend, mergeOptions, formatComponentName } from '../util/index'

// 在vue的源码中，每一个类型的实例，都会有一个唯一的标识
let uid = 0

export function initMixin (Vue: Class<Component>) {
  Vue.prototype._init = function (options?: Object) {
    const vm: Component = this   //指向当前的Vue的实例
    // a uid    Vue实例的唯一标识   在Vue的源码中，每一个类型的实例，都会有一个唯一的标识
    vm._uid = uid++


        //  用来测试性能用的，略
    let startTag, endTag
    /* istanbul ignore if */
    if (process.env.NODE_ENV !== 'production' && config.performance && mark) {
      startTag = `vue-perf-start:${vm._uid}`
      endTag = `vue-perf-end:${vm._uid}`
      mark(startTag)
    }

    // a flag to avoid this being observed
        // 一个标志，  标识不需要进行响应式处理
    vm._isVue = true
    // merge options
        /*
        问题：Vue实例 和Vue组件 有有什么不同？

        在Vue的源代码例，Vue实例还有Vue组件 在变现形式上其实最后都是落脚到 构造函数里
        合并选项，
        options._isComponent 是一个组件的时候才为true
         */
    if (options && options._isComponent) {  //是一个组件的话，用下面的方式初始化
        // 开始进行源码分析，一般都是使用简单的Vue的实例，这里是针对组件，暂时略
      // optimize internal component instantiation
      // since dynamic options merging is pretty slow, and none of the
      // internal component options needs special treatment.
      initInternalComponent(vm, options)
    } else {
      //跟自定义的option进行合并  Vue上默认的选项，和用户传进来的选项
      vm.$options = mergeOptions(
        resolveConstructorOptions(vm.constructor),
        options || {},  //用户传进来的options
        vm
      )
    }
    /* istanbul ignore else */
    if (process.env.NODE_ENV !== 'production') {
      initProxy(vm)
    } else {
      vm._renderProxy = vm
    }
    // expose real self
    vm._self = vm


    initLifecycle(vm)   // 初始化生命周期的 一些状态变量
    initEvents(vm)      // 初始化事件的 容器
    initRender(vm)      //  初始化 创建元素的方法
    callHook(vm, 'beforeCreate')    //调用生命周期函数
    initInjections(vm) // resolve injections before data/props    //初始化注入器  略
    initState(vm)         //重点   初始化状态数据 （data， property 等）
    initProvide(vm) // resolve provide after data/props   略
    callHook(vm, 'created')   //生命周期函数调用

    /* istanbul ignore if */
    if (process.env.NODE_ENV !== 'production' && config.performance && mark) {
      vm._name = formatComponentName(vm, false)
      mark(endTag)
      measure(`vue ${vm._name} init`, startTag, endTag)
    }

      //前边的代码 是组件的创建
    if (vm.$options.el) {
      vm.$mount(vm.$options.el)   //组价的挂载
        // 先调用 扩展的 mount 方法，   生成render函数
        // 再调用原始的 mount方法       获得元素，再调用 mountComponent方法
        // 这两个方法都定义在 platforms/web里面
    }
  }
}

export function initInternalComponent (vm: Component, options: InternalComponentOptions) {
  const opts = vm.$options = Object.create(vm.constructor.options)
  // doing this because it's faster than dynamic enumeration.
  const parentVnode = options._parentVnode
  opts.parent = options.parent
  opts._parentVnode = parentVnode

  const vnodeComponentOptions = parentVnode.componentOptions
  opts.propsData = vnodeComponentOptions.propsData
  opts._parentListeners = vnodeComponentOptions.listeners
  opts._renderChildren = vnodeComponentOptions.children
  opts._componentTag = vnodeComponentOptions.tag

  if (options.render) {
    opts.render = options.render
    opts.staticRenderFns = options.staticRenderFns
  }
}

export function resolveConstructorOptions (Ctor: Class<Component>) {
  let options = Ctor.options
  if (Ctor.super) {
    const superOptions = resolveConstructorOptions(Ctor.super)
    const cachedSuperOptions = Ctor.superOptions
    if (superOptions !== cachedSuperOptions) {
      // super option changed,
      // need to resolve new options.
      Ctor.superOptions = superOptions
      // check if there are any late-modified/attached options (#4976)
      const modifiedOptions = resolveModifiedOptions(Ctor)
      // update base extend options
      if (modifiedOptions) {
        extend(Ctor.extendOptions, modifiedOptions)
      }
      options = Ctor.options = mergeOptions(superOptions, Ctor.extendOptions)
      if (options.name) {
        options.components[options.name] = Ctor
      }
    }
  }
  return options
}

function resolveModifiedOptions (Ctor: Class<Component>): ?Object {
  let modified
  const latest = Ctor.options
  const sealed = Ctor.sealedOptions
  for (const key in latest) {
    if (latest[key] !== sealed[key]) {
      if (!modified) modified = {}
      modified[key] = latest[key]
    }
  }
  return modified
}
